import { Pipe, PipeTransform } from '@angular/core';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';

declare var require: any;
const PR = require('google-code-prettify/bin/prettify.min');

@Pipe({
  name: 'prettyfile'
})
export class PrettyfilePipe implements PipeTransform {

  private _entityMap: any = {
    '<': `&lt;`,
    '>': '&gt;',
    '"': '&quot;',
    '\'': '&#39;',
    '/': '&#x2F;',
    '`': '&#x60;',
    '=': '&#x3D;',
    '&': '&amp;',
  };

  constructor(private sanitizer: DomSanitizer) {}

  transform(content: string, filename: string): SafeHtml {
    const language = this._getLanguage(filename);
    let text = this._escapeHtml(content);

    text = PR.prettyPrintOne(text, language, false);
    return this.sanitizer.bypassSecurityTrustHtml(text);
  }

  private _getLanguage(filename: string): string {
    if (!filename) { return ''; }
    const split = filename.split('.');
    if (split.length <= 1) { return ''; }
    const extension = split[split.length - 1];
    switch (extension) {
      case 'scss': return 'css';
      default: return `${extension}`;
    }
  }

  private _escapeHtml(string: string): string {
    return String(string).replace(/[&<>"'`=\/]/g, (s) => this._entityMap[s] );
  }
}

import { Directive, OnInit, Input, ElementRef, HostListener, RendererV2 } from '@angular/core';
import * as Clipboard from 'clipboard';

@Directive({
  selector: '[drClipboard]'
})
export class ClipboardDirective  {

  @Input() text: string;
  private _button: any;

  @HostListener('mouseenter') onMouseEnter() {
    if (!this._button) {
      this._button = this._renderer.createElement('button');
      this._button.innerHTML = 'copy';
      this._button.className = 'copybutton mat-accent mat-raised-button';
      this._element.nativeElement.append(this._button);
      this._renderer.listen(this._button, 'click', () => { this._copyToClipboard(); return true; });
    } else {
      this._button.className = 'copybutton mat-accent mat-raised-button';
    }
  }

  @HostListener('mouseleave') onMouseLeave() {
    if (this._button) {
      this._button.className = 'hiddenbutton';
    }
  }

  constructor(private _element: ElementRef, private _renderer: RendererV2) {
  }

  private _copyToClipboard() {
    const clipboard = new Clipboard(this._element.nativeElement, { text: () => this.text });
    clipboard.on('success', (e) => { clipboard.destroy(); });
  }
}

